# case is used to initialise the variable for check
# when is used for checking
# else is for default case if nothing matches


class Car
    def drive(name)
        case name
        when "Sriram"
            puts "Sriram"
        when "Sriram"
            puts "Sriram"
        when "Surya"
            puts "Surya"
        else
            "Sriram"
        end
    end
end

test=Car.new
test.drive("Sriram")
